(function executeRule(current, previous /*null when async*/) {
 var indoorRefMapping = {
  sn_wsd_core_room: 'indoor_map_place',
  sn_wsd_core_space: 'indoor_map_place',
  sn_wsd_core_floor: 'indoor_map_floor',
  sn_wsd_core_building: 'indoor_map_building',
  sn_wsd_core_campus: 'indoor_map_campus'
 };

    var tableName = current.getTableName();

    if (indoorRefMapping[tableName] && current.external_id.changesTo('') && current[indoorRefMapping[tableName]].changesTo('')) {
        // Invalidate all previous uncompleted Indoor Mapping / WSD synchronizations
        var campusSysId = tableName === 'sn_wsd_core_campus' ? current.getUniqueValue() : current.getValue('campus');
        var syncGr = new GlideRecord('sn_wsd_indoor_map_sync');
        syncGr.addQuery('wsd_campus', campusSysId);
        syncGr.addQuery('state', '!=', 'complete');
        syncGr.addQuery('state', '!=', 'aborted');
        syncGr.setValue('state', 'aborted');
        syncGr.updateMultiple();
    }
})(current, previous);
