x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (current.isActionAborted())
  return;

 if (current.operation() == 'insert' && JSUtil.notNil(current.parent)) {
  var currentEffort = current.work_effort.getGlideObject().getNumericValue();
  var demandCostUtil = new DemandEffortUtil();
        demandCostUtil.updateActualEffortForDemandParent(current.parent.getRefRecord(), currentEffort);
 }
 else if(current.operation() == 'update' && JSUtil.notNil(current.parent) && current.work_effort.changes()){
  var oldEffort = previous.work_effort.getGlideObject().getNumericValue();
  var newEffort = current.work_effort.getGlideObject().getNumericValue(); 
  var effort = newEffort - oldEffort;
  var demandCostUtil = new DemandEffortUtil();
        demandCostUtil.updateActualEffortForDemandParent(current.parent.getRefRecord(),effort);  
 }
 else if (current.operation() == 'delete'){
  var currentEffort = current.work_effort.getGlideObject().getNumericValue() * -1;
  var demandCostUtil = new DemandEffortUtil();
        demandCostUtil.updateActualEffortForDemandParent(current.parent.getRefRecord(),currentEffort);  
 }
 else
  return;

})(current, previous);
