(function executeRule(current, previous /*null when async*/) {
 if (gs.hasRole("awa_manager") || gs.hasRole("awa_agent")) {
        var currentUser = current.member.user_document;
        var agentName = sn_connect.Conversation.getAgentName(currentUser);
        var chatInteractionUtil = new ChatInteractionUtil();
        var interaction = chatInteractionUtil.getInteractionByConversation(current.conversation);
  if (!interaction) {
   return;
  }

  var conversation = sn_connect.Conversation.get(current.conversation, 'sys_cs_conversation');
        var requesterLanguage = chatInteractionUtil.getRequesterLanguage(interaction);
  var agentLanguage = chatInteractionUtil.getAgentLanguage(interaction);
        if (current.member_type == 'private_fulfiller') {
   var memberUserId = current.member.user_document;
   var memberName = sn_connect.Conversation.getAgentName(memberUserId);
   chatInteractionUtil.sendSystemMessage(conversation, '{0} has left the private conversation.', requesterLanguage, agentLanguage, memberName, true, null);
        } else if (current.member_type == 'public_fulfiller') {
   var message = '{0} has left.';

   // Use the configurable chat system message, if the agent left due to queue transfer
   var stackTrace = GlideLog.getStackTrace(new Packages.java.lang.Throwable());
   if (stackTrace.contains("com.snc.awa.domain.assignment.TransferLogic.transferToQueue")) {
    var transferMessage = gs.getProperty("com.glide.cs.agent_transfer_msg");
    if (!gs.nil(transferMessage))
     message = transferMessage;
   }

   var internal = interaction.type == 'messaging' || !interaction.active;
   chatInteractionUtil.sendSystemMessage(conversation, message, requesterLanguage, agentLanguage, agentName, internal, null);
        }
    }

})(current, previous);
