(function executeRule(current, previous /*null when async*/) {
 // Add your code here
 var budgetPeriodGR = new GlideRecord('itfm_budget_period');
 budgetPeriodGR.addQuery('open_flag', true);
 budgetPeriodGR.addQuery('budget_period', current.getElement('forecast_period').getRefRecord().getValue('fiscal_year'));
 budgetPeriodGR.query();

 if(budgetPeriodGR.next()) {
  gs.addErrorMessage(gs.getMessage('Budget Period {0} is Open', budgetPeriodGR.getDisplayValue(budgetPeriodGR.getDisplayName())));
  if(current.operation() == 'update' && current.open_flag.changes()) {
   /* Check box stays ticked even after UPDATE is aborted. Hence this revert. */
   current.setValue('open_flag', false);
  }
  current.setAbortAction(true);
 }
})(current, previous);
