x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var spotlightGroupName = GlideStringUtil.escapeHTML(current.getValue('name'));
    var runAsDomainId = getUserDomainId(current.getValue('run_as'));
    if (gs.nil(runAsDomainId)) {
        gs.addInfoMessage(gs.getMessage("{0} has an empty Run as user. Scores will be evaluated for the global domain. If you want to collect from a different domain, please select a Run as user from that domain.", spotlightGroupName));
        return;
    }
    if (runAsDomainId !== current.getValue('sys_domain')) {
        var currentDomainDisplayName = new GlideDomainDisplay(current.getValue('sys_domain')).IDToDisplay();
        var runAsDomainDisplayName = new GlideDomainDisplay(runAsDomainId).IDToDisplay();
        var message = gs.getMessage("The {0} Spotlight Group is in the {1} domain, but the domain of the Run as user is {2}. Scores will be evaluated only for the domain of the Run as user.", [spotlightGroupName, currentDomainDisplayName, runAsDomainDisplayName]);
        var messageNonGlobal = gs.getMessage("If the run as user does not have access to {0} domain, the collection will fail.", currentDomainDisplayName);

        if (currentDomainDisplayName != 'global')
            message += ' ' + messageNonGlobal;

        var messageSwitchDomain = gs.getMessage("To collect scores in the {0} domain, click Copy to Domain, copy this Spotlight Group to {0}, and select a Run as user from that domain.", runAsDomainDisplayName);

        var loginDomainName = new GlideDomainDisplay(SNC.PADomainUtils.getCurrentUserDomain()).IDToDisplay();
        if (gs.hasRole('pa_spotlight_copy_domain') && runAsDomainDisplayName != 'global' && loginDomainName.indexOf(currentDomainDisplayName) == 0)
            message += ' ' + messageSwitchDomain;

        gs.addErrorMessage(message);
        current.run_as = previous.run_as;
        current.setAbortAction(true);
    }

})(current, previous);

//Need to query with queryNoDomain() to get the domain of Run as even if it is not visible to current domain.
function getUserDomainId(userSysId) {
    var runAsDomainId = '';
    var gr = new GlideRecord('sys_user');
    gr.addQuery('sys_id', userSysId);
    gr.queryNoDomain();
    if (gr.next())
        runAsDomainId = gr.getValue('sys_domain');

    return runAsDomainId;
}
