(function executeRule(current, previous /*null when async*/) {
 var BAD_PARENT_OUTCOME = gs.getMessage("Outcome records may only have Decision records for parents. Please change the Parent to a valid Decision and resubmit.");

 var BAD_PARENT_NODE = gs.getMessage("Only Outcomes may have a Decision as a parent record. Please change the Parent to a valid, non-decision record and resubmit.");

 if (current.getRecordClassName() == "sys_cb_outcome") {
  if (current.getValue("parent"))
   if (current.parent.getRefRecord().getRecordClassName() !== "sys_cb_decision") {
    gs.addInfoMessage(BAD_PARENT_OUTCOME);
    current.setAbortAction(true);
   }
 }

 if (current.getRecordClassName() != "sys_cb_outcome") {
  if (current.getValue("parent"))
   if (current.parent.getRefRecord().getRecordClassName() == "sys_cb_decision") {
    gs.addInfoMessage(BAD_PARENT_NODE);
    current.setAbortAction(true);
   }
 }
})(current, previous);
