x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (JSUtil.notNil(current.getValue('parent'))){
  if (current.parent.getRefRecord() &&
  (current.parent.getRefRecord().instanceOf('pm_project_task') || current.parent.getRefRecord().instanceOf('pm_project')))
  preventInvalidRelationship();
 }

 function preventInvalidRelationship() {
  var plannedTask = new GlideRecord("planned_task");
  plannedTask.get(current.parent);
  if (plannedTask != null) {      
   var api = new SNC.PlannedTaskAPI();
   var projectId = current.parent.top_task;
   var result = api.isRelationValid(projectId, current);
   var msgArray = [];
   msgArray.push(current.parent.getDisplayValue());
   msgArray.push(current.child.getDisplayValue());
   if ( result == "relation_to_self" ) {
      gs.addErrorMessage(gs.getMessage('The relationship between {0} & {1} cannot be created as it creates a itself relationship ',  msgArray));
    current.setAbortAction(true);   
   }
   else if ( result == "relation_exists" ) {
     gs.addErrorMessage(gs.getMessage('The relationship between {0} & {1} cannot be created as it is a duplicate',  msgArray));
    current.setAbortAction(true);   
   }
   else if ( result == "relation_between_projects" ) {
    gs.addErrorMessage(gs.getMessage('The relationship between {0} & {1} cannot be created as they belong to different projects',  msgArray));
    current.setAbortAction(true);               
   }
   else if ( result == "relation_cyclic" || result == 'cyclic_exception' ) {
      gs.addErrorMessage(gs.getMessage('The relationship between {0} & {1} cannot be created as it creates a recursive relationship ',  msgArray));
    current.setAbortAction(true);               
   } else if(result == 'relation_successor_start_date_before_project_start_date') {
    gs.addErrorMessage(gs.getMessage('The relationship between {0} & {1} cannot be created as it makes successor start date before project start date ',  msgArray));
    current.setAbortAction(true);
   }
  }
 }

})(current, previous);
