(function executeRule(current, previous) {

 handleStateChange();

})(current, previous);

function handleStateChange(){
 var stateUtil = new PlannedTaskStateUtil(current);
 var stateBucket = stateUtil.getBucketForState(current.state);
 var program = new Program(current);
 if(stateBucket == PlannedTaskStateUtil.CLOSE_STATES){
  var canClose = program.canClose();
  if(!canClose){
   gs.addErrorMessage(gs.getMessage('Cannot close a program with active program tasks, demands or projects'));
   current.setAbortAction(true);
  }
 }else if(stateBucket == PlannedTaskStateUtil.OPEN_STATES || stateBucket == PlannedTaskStateUtil.PENDING_STATES){
  var isAllowed = program.canMovetoPendingOrOpenState();
  if(!isAllowed){
   gs.addErrorMessage(gs.getMessage('Cannot move program to {0} state with in progress program tasks, demands or projects', current.getDisplayValue('state')));
   current.setAbortAction(true);
  }
 }
}
