(function executeRule(current, previous) {
 var functionalCurrency  = current.cost_plan.functional_currency;
 if(current.entered_currency != functionalCurrency){
  var fpService = new FinancialsForPPM();
  var exchangeRates = fpService.getExchangeRates([current.fiscal_period],current.entered_currency,functionalCurrency);
  if(exchangeRates.exchangeRatesSysIds.length){
   current.fx_rate = exchangeRates.exchangeRatesSysIds[0];
   current.exchange_rate = exchangeRates.exchangeRates[0];
   current.exchange_rate_date = exchangeRates.exchangeRateDates[0];
   var currencyService = new SNC.FMCurrency();
   if(exchangeRates.exchangeRatesSysIds[0])
    current.cost_default_currency = currencyService.calculateAmount(current.cost_local_currency, exchangeRates.exchangeRatesSysIds[0]);
   else{
    current.cost_default_currency = current.cost_local_currency;
   }

  }
 else {
  current.cost_default_currency = current.cost_local_currency;
  current.fx_rate = "";
  current.exchange_rate = "";
 }

 }
 else{
  current.cost_default_currency = current.cost_local_currency;
  current.fx_rate = "";
  current.exchange_rate = "";
 }

 if ((current.entered_currency == functionalCurrency) && (functionalCurrency == current.project_currency))
  current.exchange_rate_date = "";

})(current, previous);
