x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (isOOBConfig(current)) {
        g_scratchpad.isOOB = 'true';
    }

 function isOOBConfig(current) {
    //Microsoft translator sys_ids
    var MICROSOFT_DETECT_SYS_ID_V3 = '1f06bdfc5372101026b0ddeeff7b1250';
    var MICROSOFT_TRANSLATE_SYS_ID_V3 = '219b23cd5342101026b0ddeeff7b126a';

    //IBM translator sys_ids
    var IBM_DETECT_SYS_ID_V3 = '4d5bf11e5372101026b0ddeeff7b1222';
    var IBM_TRANSLATE_SYS_ID_V3 = 'f84744c95382101026b0ddeeff7b12ce';

 //ServiceNow Langauge Detection sysids
    var LDS_DETECT_SYS_ID_V3 = '66a95875c723101020dab6c427c26052';

    //Check for Microsoft OOB configuration record
    if (current.getValue('detect_sub_flow') === MICROSOFT_DETECT_SYS_ID_V3 &&
   current.getValue('translate_sub_flow') === MICROSOFT_TRANSLATE_SYS_ID_V3 &&
   current.getValue('name') === 'Microsoft' &&
   current.getValue('version') === 'v3') {
        return true;
    }
    //Check for IBM OOB configuration record
    else if (current.getValue('detect_sub_flow') === IBM_DETECT_SYS_ID_V3 &&
   current.getValue('translate_sub_flow') === IBM_TRANSLATE_SYS_ID_V3 &&
   current.getValue('name') === 'IBM' &&
   current.getValue('version') === 'v3') {
        return true;
    }
    //Check for ServiceNow OOB configuration record
    else if (current.getValue('detect_sub_flow') === LDS_DETECT_SYS_ID_V3 &&
   !current.getValue('translate_sub_flow') &&
   current.getValue('name') === 'ServiceNow' &&
   current.getValue('version') === 'v3') {
        return true;
    }
    return false;
}

})(current, previous);
