x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 //To prevent allocation dailies getting created/modified, if invalid dates given while creating/updating resource allocation record manually. 
 if(current.isActionAborted())
  return;

 /**
  * decicison is to stop the resource_allocation date updates when it has resource_assignment
  */
 if(current.operation() == "update" && !gs.nil(current.getValue('resource_assignment')) && (current.start_date.changes() || current.end_date.changes())) {
  return;
 }

 if(typeof dailyRecordUpdated === 'undefined' || dailyRecordUpdated != true) {
 var inValidPlanStates = {1 : "Planning", 4 : "Rejected", 5: "Change", 6: "Work In Progress",7: "Completed", 8: "Cancelled", 10: "Cancel in progress", 9: "Allocation in progress"};
 var state = current.resource_plan.state;
 var inValidPlanState = false;
 if(inValidPlanStates[state])
  inValidPlanState = inValidPlanStates[state];
 if(inValidPlanState) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage("Resource cannot be allocated when the plan is in {0} state", inValidPlanState));
 }
 else {
  var ra = new ResourceAllocation(current);
  if(current.user.changes() && current.operation()=="update")
   ra.deleteEvents(true);
    //If the resource allocation table's user column is filled up, we are using smart even spread logic (taking availability into account); 
  //otherwise, we are defaulting to even spread (without considering availability)
  if(gs.nil(current.getValue('user'))) {
   ra.createEvents(false, false, false);
  } else {
      var infoMsg = ra.updateDailies();
      var currencyCode = current.allocated_cost.getReferenceCurrencyCode();
      ra.calculateAndSetAllocatedHoursAndCost(currencyCode,ra.dateRange());
      ra.calculateAndSetManDaysAndFteFromAllocatedHours();
      if(infoMsg)
        gs.addInfoMessage(infoMsg);
  }
    //updating extension to completed when an allocation is edited
    var rpGr = current.resource_plan.getRefRecord();
    if(rpGr.getValue('extension') == 'requested') {
      rpGr.setValue('extension', 'completed');
      rpGr.setWorkflow(false);
      rpGr.update();
    }
 }
}
})(current, previous);
