var work_filter = "";
var tableName = current.sys_class_name;
if (tableName == "wm_task") {
 var wf = new global.WMFilters();
 var isTerritoryPlanningActive = wf.isTerritoryPlanningActive();
 var smConfigCacheManager = new SMConfigCacheManager();
 var smConfig = smConfigCacheManager.getSMConfig(tableName);
 if (smConfig.dispatch_queue == 'true' && !isTerritoryPlanningActive) {
  var filter = new SMUtils().filterGroups(current.sys_class_name, current.location, 'dispatch');
  // clear out dispatch group if current value is not applicable anymore
  if (!current.dispatch_group.nil() && filter.indexOf(current.dispatch_group.toString()) == -1)
   current.dispatch_group = '';
  // if only one possible group, auto populate
  if (filter.indexOf('sys_idIN') == 0 && filter.length > 8 && filter.length < 50)
   current.dispatch_group = filter.substring(8, 40);
 }

 if(isTerritoryPlanningActive){
  new sn_fsm_tp.FieldServiceTerritoryPlanning().validateTerritoryWorkAssignments(current);
 } else {
  wf.autoPopulateAssignmentGroupHelper(current);
 }

} else {
 if ((new sn_sm.SMConfiguration()).isEnabled(current, 'dispatch_queue', false)) {
  var filter = new SMFilters().filterGroups(current.sys_class_name, current.location, 'dispatch');
  // clear out dispatch group if current value is not applicable anymore
  if (!current.dispatch_group.nil() && filter.indexOf(current.dispatch_group.toString()) == -1)
   current.dispatch_group = '';
  // if only one possible group, auto populate
  if (filter.indexOf('sys_idIN') == 0 && filter.length > 8 && filter.length < 50)
   current.dispatch_group = filter.substring(8, 40);
    }

 if(!(gs.nil(current.assignment_group))){
  new SMFilters().validateAssignmentGroup(current);
 }
 if(gs.nil(current.assignment_group)){
  // Autopopulate assignment group if only one assignment group is available (not including vendor groups) and no group is selected
  work_filter = new SMFilters().getValidAssignmentGroups(current); //ignoreVendors - true
  if (current.assignment_group.nil() && work_filter.indexOf('sys_idIN') == 0 && work_filter.length > 8 && work_filter.length < 50)
   current.assignment_group = work_filter.substring(8, 40);
 }
}
