x_938076_now_utils_now_utils_business_ru.script:
/**
 * Return the list of variables which are Reference fields in a conversation
 * 
 * We have to find the fields which are References so we can set up templates
 * and record watchers based on which records are specified in said variables.
 * However, simply referencing the variable isn't enough, as the result will be
 * the column name, not the table it references. So we have to actually query
 * the var_dictionary to get the underlying table.
 */
function sys_cb_action_varsGetChoices() {


 // if we don't have current, we can't figure out the variable table
 if (typeof current == "undefined" || JSUtil.nil(current) || !current || JSUtil.nil(current.sys_id)) {
  gs.log("Error generating Topic reference variable choices: current record is undefined or empty");
  return;
 }

 //answer should be automatically defined in our context when this function is called
 if (!answer) {
  gs.log("Error generating Topic reference variable choices: answer is undefined");
  return;
 }
 var variable_table = current.getValue("variable_table");
 var variable_lookup = new TopicVariableAsChoice();

 var choices = variable_lookup.getReferences(variable_table);

 for (var i=0; i< choices.length; i++) {
  var choice = choices[i];
  answer.add(choice.value, choice.label);
 } 
}
