x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (current.getValue('enable_projects') == '1') {
        var artifact = current.getValue('artifact');
        if (artifact == 'all_artifacts') {
            var artifactsWithNoProcessorScripts = new LFArtifactUtils().getAllArtifactsWithNoProcessorScripts();
            if (artifactsWithNoProcessorScripts.length > 0) {
                gs.addErrorMessage(gs.getMessage("Following artifacts do not have processor script: {0}.", artifactsWithNoProcessorScripts.toString()));
                current.setAbortAction(true);
            }
        } else {
            var hasProcessorScript = new LFArtifactUtils(artifact).hasProcessorScript();
            if (!hasProcessorScript) {
                gs.addErrorMessage(gs.getMessage("Following artifacts do not have processor script: {0}.", artifact));
                current.setAbortAction(true);
            }
        }
    }
})(current, previous);
