x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var interval = current.assignment_interval;
 if(interval < 10) {
  var message = gs.getMessage("Auto assignment interval cannot be less than 10 minutes");
  gs.addErrorMessage(message);
  current.setAbortAction(true);
 }
 else {
  var filterGR = new GlideRecord(current.getTableName());
  filterGR.addQuery("dynamic_scheduling_config", current.dynamic_scheduling_config);
  filterGR.addEncodedQuery("auto_assign=true");
  filterGR.addEncodedQuery("assignment_frequency=interval");
  filterGR.addQuery("assignment_interval", ">", current.assignment_interval);
  filterGR.addQuery("order", "<", current.order);
  filterGR.orderByDesc("assignment_interval");
  filterGR.setLimit(1);
  filterGR.query();
  if(filterGR.getRowCount() > 0){
   filterGR.next();
   var message = gs.getMessage("Auto assignment interval cannot be less than {0} minutes",filterGR.assignment_interval);
   gs.addErrorMessage(message);
   current.setAbortAction(true);
   return;
  }

  filterGR = new GlideRecord(current.getTableName());
  filterGR.addQuery("dynamic_scheduling_config", current.dynamic_scheduling_config);
  filterGR.addEncodedQuery("auto_assign=true");
  filterGR.addEncodedQuery("assignment_frequency=interval");
  filterGR.addQuery("assignment_interval", "<", current.assignment_interval);
  filterGR.addQuery("order", ">", current.order);
  filterGR.order("assignment_interval");
  filterGR.setLimit(1);
  filterGR.query();
  if(filterGR.getRowCount() > 0){
   filterGR.next();
   var message = gs.getMessage("Auto assignment interval should be less than {0} minutes", filterGR.assignment_interval);
   gs.addErrorMessage(message);
   current.setAbortAction(true);
  }


 }



})(current, previous);
