x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {  
 var isCostPlanBatch = (typeof costPlanBatch !== 'undefined') && JSUtil.notNil(costPlanBatch);
 if(isCostPlanBatch && costPlanBatch)
   return;
    // Safety check
 var task = current.task.getRefRecord();
 var needToUpdateProjectCurrencyFields = (current.cost_plan.project_currency && (task.instanceOf('pm_project') || task.instanceOf('pm_project_task'))) ? true : false;
 var needToUpdateDemandCurrencyFields = (current.cost_plan.demand_currency && task.instanceOf('dmn_demand')) ? true : false;

    if (JSUtil.nil(current.getValue("cost_plan")))
        return;

    //If inserting or updating cost plan, need not to calculate total planned cost and
    //total actual cost for each breakdown
    var isOriginatingFromCostPlan = (typeof origCostPlanId !== 'undefined') && JSUtil.notNil(origCostPlanId);
    if(isOriginatingFromCostPlan && origCostPlanId == current.getValue('cost_plan'))
        return;

    var costPlan = new CostPlan(current.cost_plan.getRefRecord());
    var updatedTotalPlannedCost = false;
    if(current.cost_local_currency.changes())
        updatedTotalPlannedCost = costPlan.updateTotalPlannedCost();

 var updatedTotalProjectPlannedCost = false;
 if(needToUpdateProjectCurrencyFields && current.cost_local_currency.changes())
  updatedTotalProjectPlannedCost = costPlan.updateTotalProjectPlannedCost();

 var updatedTotalDemandPlannedCost = false;
 if(needToUpdateDemandCurrencyFields && current.cost_local_currency.changes())
  updatedTotalDemandPlannedCost = costPlan.updateTotalDemandPlannedCost();


    var updatedTotalActualCost = false;
    if(current.actual.changes())
        updatedTotalActualCost = costPlan.updateTotalActualCost();

    var updated = updatedTotalPlannedCost || updatedTotalActualCost || updatedTotalProjectPlannedCost || updatedTotalDemandPlannedCost;
    if(updated)
        costPlan.update();

})(current, previous);
