(function executeRule(current, previous /*null when async*/) {

 for (var i = 1; i <= 6; i++) {
  var fieldInfo = isNotValidListField(current.coe, current.getValue('person_' + i));
  if (current.getValue('person_column_' + i) == 1 && fieldInfo.notValidField) {
   gs.addErrorMessage(gs.getMessage('\'{0}\' must be a user or a list of users', fieldInfo.label));
   current.setAbortAction(true);
  }
 }
})(current, previous);

/*
  Evaluates whether the fieldName is a valid glide list record and return JSON object containing the following properties
  1. label - Label of the field from sys_dictionary
  2. notValidField - true, that indiciates whether a field is  not a sys_user or a list of sys_user record.
 */
function isNotValidListField(tableName, fieldName) {
  var gr = new GlideRecord(tableName);
  var element = gr.getElement(fieldName);
  var descriptor =  element.getED();
  var fieldInfo = {}; 
  fieldInfo.notValidField = false;
  fieldInfo.label = fieldName;
  if (!gs.nil(descriptor)) {
   var elementReference;
   // TODO PRB1086657: element.getED().getReference() is not available in scope yet
   var elementDictionaryGr = new GlideRecord("sys_dictionary");
   elementDictionaryGr.addQuery("name", descriptor.getTableName());
   elementDictionaryGr.addQuery("element", fieldName);
   elementDictionaryGr.query();
   if (elementDictionaryGr.next()) {
    elementReference = elementDictionaryGr.getValue("reference");
    fieldInfo.label = elementDictionaryGr.getValue("column_label");
   }
   var tableExtensions  = new GlideTableHierarchy('sys_user').getAllExtensions();
   //notValidReference will return true if elementReference is not contained in the tables extended by 'sys_user' table
    var notValidReference = String(tableExtensions).indexOf(elementReference) < 0;
    if (descriptor.getInternalType() == 'glide_list' && notValidReference)
     fieldInfo.notValidField = true;
  } 
  return fieldInfo;
}
