(function executeRule(current, previous /*null when async*/) {
 //exempt admin & system user, and always allow rejection due to "No access"
 if (gs.hasRole('admin') || current.reason == "dd05547073ad23004a905ee515f6a740") {
  return;
 }
 if (current.work_item.state != "pending_accept") {
  gs.addErrorMessage(gs.getMessage("Rejection allowable only from the pending_accept state"));
  current.setAbortAction(true);
  return;
 }
 if (current.work_item.assigned_to != gs.getUserID()) {
  // Allow rejection if the user has awa_integration_user role
  if (gs.hasRole('awa_integration_user'))
   return;
  gs.addErrorMessage(gs.getMessage("You can only reject work items that were offered to you or you need awa_integration_user role"));
  current.setAbortAction(true);
  return;
 }
 if (current.work_item.allocated_by != "")  // allow rejection if it's a manual allocation
  return;
 if (current.work_item.previous_work_item) // allow rejection if it's a transfer
  return;
 if (!current.work_item.assignment_rule.allow_agents_to_decline) {
  gs.addErrorMessage(gs.getMessage("This work item cannot be rejected."));
  current.setAbortAction(true);
  return;
 }
})(current, previous);
