x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var empProfile = new employee_profile().getCurrentEmployeeProfile(current.user.sys_id);

    if (empProfile) {
        empProfile.employment_start_date = current.employment_start_date;
        empProfile.employment_end_date = current.employment_end_date;
        empProfile.work_phone = current.work_phone;
        empProfile.work_mobile = current.work_mobile;
        empProfile.location_type = current.location_type;
        empProfile.position_type = current.position_type;
        empProfile.hr_profile = current.sys_id;
        empProfile.update();
    }
    //Insertion is handled based on employee definition.
    else {
        var epUtil = new sn_employee.ep_Utils();
        var isDomainSeparationActive = GlidePluginManager.isActive("com.glide.domain.msp_extensions");
        var domainId = current.getValue("sys_domain");
        var empDefinition = isDomainSeparationActive ? epUtil.getCurrentDefinitionInDomain(domainId) : epUtil.getCurrentDefinition();
        if (empDefinition) {
            if (empDefinition.table.toString() === 'sn_hr_core_profile' && epUtil.userSatisfiesEmployeeDefintion(current.user.sys_id, empDefinition)) {
                epUtil.createEmployeeProfileFromHRProfile(current);
            }
        } else {
            epUtil.createEmployeeProfileFromHRProfile(current);
        }
    }

}(current, previous));
