(function executeRule(current, previous) {
 current.setAbortAction(true);
 var arguments = [current.indicator.name, getDisplayName(previous.indicator), getDisplayName(current.indicator)];
 var message1 = gs.getMessage('You cannot change the main indicator of this Spotlight Group to "{0}" because it changes the facts table from {1} to {2}. The query criteria and indicator criteria are based on facts table {1}.', arguments);
 var message2 = gs.getMessage('You can either change the main indicator to another main indicator that uses facts table {1} or delete all of the existing criteria and replace those with criteria based on facts table {2}.', arguments);
 gs.addErrorMessage(message1 + ' ' + message2);
})(current, previous);

function getDisplayName(factsTable) {
 return GlideTableDescriptor.get(SNC.SpotlightUtil.getFactsTable(factsTable)).getLabel();
}
