x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.account != previous.account || current.consumer != previous.consumer) {

        var count = 0;
        var gr = new GlideAggregate("sn_install_base_m2m_affected_install_base");
        if (current.account != previous.account)
            gr.addAggregate('COUNT', 'install_base_item.account');
        else
            gr.addAggregate('COUNT', 'install_base_item.consumer');
        gr.addQuery('csm_case', current.sys_id);
        gr.query();
        if (gr.next())
            count = gr.getRowCount();
        if (count > 1) {
            current.setAbortAction(true);
            if (current.account != previous.account)
                gs.addErrorMessage(gs.getMessage('Delete affected install base items before changing the account'));
            else
                gs.addErrorMessage(gs.getMessage('Delete affected install base items before changing the consumer'));
        } else if (count == 1) {
            var m2mgr = new GlideRecord('sn_install_base_m2m_affected_install_base');
            m2mgr.addQuery('csm_case', current.sys_id);
            m2mgr.query();
            if (m2mgr.next()) {

                if (current.account != previous.account && m2mgr.install_base_item.account != current.account) {
                    current.setAbortAction(true);
                    gs.addErrorMessage(gs.getMessage('Delete affected install base items before changing the account'));
                    return;
                }
                if (current.consumer != previous.consumer && m2mgr.install_base_item.consumer != current.consumer) {
                    current.setAbortAction(true);
                    gs.addErrorMessage(gs.getMessage('Delete affected install base items before changing the consumer'));
                    return;
                }
            }
        }
    }
})(current, previous);
