(function executeRule(current, previous /*null when async*/ ) {
    if (current.active == false && previous.active == false) {
        var readOnlyFields = [
            "source_record", "source_table", "state", "request_source",
            "reason", "type", "escalation_severity", "trend", "escalation_justification"
        ];
        // Preventing any updates to above fields when the escalation is read-only.
        var len = readOnlyFields.length;
        for (var i = 0; i < len; i++) {
            var fieldName = readOnlyFields[i];
            if (current.getValue(fieldName) != previous.getValue(fieldName)) {
                gs.addErrorMessage(gs.getMessage("You cannot make changes to the inactive escalation."));
                current.setAbortAction(true);
                break;
            }
        }
    }


})(current, previous);
