x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {


 if(gs.getProperty("com.snc.project.sync_original_dates_with_planned_dates", "true") == "true"){
  syncScheduleDatesWithPlannedDates();
 }
 else{
  var workStartChanges = current.work_start.changes();
  var workStart = current.getValue("work_start");
  var rollup = current.getValue("rollup");
  if ( JSUtil.nil(workStart) && !workStartChanges && !(rollup == '1' || rollup == 'true')) {
   gs.print("Updating Planned Dates from current dates - WorkStart -> " + workStart);
   syncScheduleDatesWithPlannedDates();
  }
  else if(current.isNewRecord() && !JSUtil.nil(workStart)) { //Planned and Original dates will be same for a new task
   syncScheduleDatesWithPlannedDates();
  }
 }

})(current, previous);

function syncScheduleDatesWithPlannedDates(){
 current.schedule_start_date = current.start_date;
 current.schedule_end_date = current.end_date;
}
