x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var portfolioProject = new GlideRecord('pm_portfolio_project');
 portfolioProject.get(current.project);
 var project = new GlideRecord("pm_project");
 project.get(portfolioProject.pm_project);

 if (current.operation() == "insert" && project.primary_portfolio.nil()) {
  project.primary_portfolio = current.portfolio;
  project.top_portfolio = current.portfolio;
 } else if (current.operation() == "delete" && !project.primary_portfolio.nil() && project.primary_portfolio == current.portfolio) {
  project.primary_portfolio = "";
  project.top_portfolio = "";
 }

 if (project.changes()) {
  project.setWorkflow(false);
  project.update();
  var synchronizer = new PmProgramPortfolioSynchronizer(project);
  synchronizer.updateRelatedEntitiesForPlannedTask();
  if(!project.primary_program.nil() && project.sys_id == project.top_task){
   var pgmSynchronizer = new ProgramRelatedPortfoliosSync(project);
   pgmSynchronizer.run();
  }
 }

})(current, previous);
