(function executeRule(current, previous /*null when async*/) {
 var timesheetPolicy = TimeSheetPolicy.getFromUserId(gs.getUserID());
 var isAllowMultipleRateTypes = timesheetPolicy.allowMultipleRatetypes();
 if(!isAllowMultipleRateTypes)
  return;

 var queryValues = {
  task: current.task,
  category: current.category,
  project_time_category: current.project_time_category,
  rate_type: current.rate_type
 };

 var query = TimeCardUtil.getTimeCardFilter(queryValues);
 var timesheet = new TimeSheet(current.time_sheet.getRefRecord());
 var existingTimecard = timesheet.getTimeCards(query);
 if(existingTimecard.getRowCount() > 0) {
  existingTimecard.next();
  var values = {
   sunday: current.sunday,
   monday: current.monday,
   tuesday: current.tuesday,
   wednesday: current.wednesday,
   thursday: current.thursday,
   friday: current.friday,
   saturday: current.saturday
  };
  TimeCardUtil.setValues(existingTimecard, values, false);
  existingTimecard.update();
  current.deleteRecord();
 } 

})(current, previous);
