x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {

 var stateUtil = new PlannedTaskStateUtil(current);
 var stateBucket = stateUtil.getBucketForState(current.state);
 if (stateBucket == PlannedTaskStateUtil.WORK_IN_PROGRESS) {
  current.work_start.setDisplayValue(gs.nowDateTime());
  if(!previous.active){
   current.setValue('work_end', ''); //clear actual end in case of restarting/opening task
   current.setValue('work_duration', '');
  }
  if(!current.rollup){
   current.setValue('percent_complete', 0);
  }
 }else if(!stateUtil.isStateInactive(current.state)) {
  current.setValue('work_start', '');
  current.setValue('work_end', ''); //clear actual end in case of restarting/opening task
  current.setValue('work_duration', '');
  current.setValue('percent_complete', 0);
 } else {
  if(current.work_start.nil())
   current.setDisplayValue('work_start',gs.nowDateTime());
  if(current.work_end.nil())
   current.setDisplayValue('work_end', gs.nowDateTime());
  current.work_duration = gs.dateDiff(current.work_start.getDisplayValue(), current.work_end.getDisplayValue(), false);
  current.percent_complete = 100;
 }

})(current, previous);
