x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var abort = false;
    var operation = current.operation();
    var wpLocationMigrationConfigUtils = new WPCmnLocationMigrationUtils();

    if (current.active && !wpLocationMigrationConfigUtils.validateRootLevel(current)) {
        gs.addErrorMessage(gs.getMessage('The parent for this location migration has already been set to empty and there cannot be more than one empty parent'));
        abort = true;
    }

    if (operation == 'update' && current.active && current.wsd_location_table.changes() && wpLocationMigrationConfigUtils.usedAsParentTableInLocationMigrationConfig(previous)) {
        gs.addErrorMessage(gs.getMessage('You cannot change the Workplace location table as there are other location migrations that are using this workplace location as a parent'));
        abort = true;
    }

    if (operation != 'delete' && current.active && !wpLocationMigrationConfigUtils.validateForParentTable(current)) {
        gs.addErrorMessage(gs.getMessage('You cannot change the default location migration hierarchy'));
        abort = true;
    }

    if (operation != 'delete' && current.active && !wpLocationMigrationConfigUtils.isLocationMigrationConfigUnique(current)) {
        gs.addErrorMessage(gs.getMessage('A location migration configuration for this Workplace location table already exists'));
        abort = true;
    }

    if (operation != 'delete' && current.active && !wpLocationMigrationConfigUtils.isParentLocationMigrationConfigAvailable(current)) {
        gs.addErrorMessage(gs.getMessage('A location migration configuration is not defined for the selected parent. Please create a location migration configuration for the parent and try again'));
        abort = true;
    }

    if (((operation == 'delete' && current.active) || (current.active.changes() && !current.active)) && wpLocationMigrationConfigUtils.usedAsParentTableInLocationMigrationConfig(current)) {
        gs.addErrorMessage(gs.getMessage('You cannot delete/de-activate this location migration configuration as it is used by other location migrations as a parent'));
        abort = true;
    }

    if (abort)
        current.setAbortAction(true);


})(current, previous);
