(function executeRule(current, previous /*null when async*/) {

 var OUTCOME_MESSAGE = gs.getMessage("There are conversation paths that would be orphaned if this Decision Outcome record is deleted. Please delete those conversation paths before deleting this Decision Outcome");
 var DECISION_MESSAGE = gs.getMessage("There are conversation paths that would be orphaned if this Decision record is deleted. Please delete those conversation paths before deleting this Decision");
 var COLLECTOR_MESSAGE = gs.getMessage("There are conversation paths that would be orphaned if this Thread Colletor record is deleted. Please delete those conversation paths before deleting this Thread Colletor.");
 var DEFAULT_MESSAGE = gs.getMessage("There are conversation paths that would be orphaned if this record is deleted. Please delete those conversation paths before deleting this Thread Colletor.");

 var nodeUtil = new ConversationNodeUtil();
 if (!nodeUtil.canNodeBeDeleted(current)) {
  current.setAbortAction(true);
  gs.debug("Aborting delete of node {0} ({1})", current.getValue("name"), current.getValue("sys_id"));

  switch (current.getRecordClassName()) {
   case "sys_cb_decision":
    gs.addInfoMessage(DECISION_MESSAGE);
    break;
   case "sys_cb_outcome":
    gs.addInfoMessage(OUTCOME_MESSAGE);
    break;
   case "sys_cb_collector":
    gs.addInfoMessage(COLLECTOR_MESSAGE);
    break;
   default :
    gs.addInfoMessage(DEFAULT_MESSAGE);
  }
  return;
 }
})(current, previous);
